import React from "react";
import classes from "./chooseBtn.module.scss";
import Modal from "../modals/Modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown } from "@fortawesome/free-solid-svg-icons";

const ChooseBtn = ({ modal, setModal, data }) => {
  const categories = data.map((obj) => Object.keys(obj)[0]);

  return (
    <>
      <button
        className={classes.chooseBtn}
        onClick={(e) => {
          e.preventDefault();
          setModal(true);
        }}
      >
        All categories
        <FontAwesomeIcon icon={faAngleDown} />
      </button>
      <Modal setVisible={setModal} visible={modal}>
        <input type="radio" name="chooseCategory" id={`${item}-id-modal`} />
        <label for="all-id-modal" id={`${item}-id-modal`}>
          All
        </label>
        <ul>
          {categories.map((item) => (
            <li>
              <input
                type="radio"
                name="chooseCategory"
                id={`${item}-id-modal`}
              />
              <label for={`${item}-id-modal`}>{item}</label>
            </li>
          ))}
        </ul>
      </Modal>
    </>
  );
};

export default ChooseBtn;
