import React, { useState, useRef, useEffect } from "react";
import classes from "./searchForm.module.scss";
import SearchIcon from "../../trifles/icons/SearchIcon";
import ChooseBtn from "./ChooseBtn";

const SearchForm = ({modal, setModal}) => {

  const [query, setQuery] = useState("");
  const [inputActive, setInputActive] = useState(false);
  const [promptShown, setPromptShown] = useState(false);

  const searchQueryChange = (event) => {
    setQuery(event.target.value);

    if (event.target.value != 0) {
      setInputActive(true);
      setPromptShown(true);
    } else {
      setInputActive(false);
      setPromptShown(false);
    }
  };

  const promptClasses = [classes.prompt];

  if (promptShown === false) {
    promptClasses.push(classes.hidden);
  }

  const formClasses = [classes.searchForm];

  if (inputActive) {
    formClasses.push(classes.active);
  }

  const inputRef = useRef(null);

  const inputClick = () => {

  }

  const ulRef = useRef(null);

  useEffect(() => {
    const onClick = (e) => {
      if (!ulRef.current.contains(e.target)) {
        setPromptShown(false);
        setInputActive(false);
      }
    };
    document.addEventListener("click", onClick);
    return () => document.removeEventListener("click", onClick);
  }, []);


  return (
    <form className={formClasses.join(" ")}>
      <ChooseBtn modal={modal} setModal={setModal}/>
      <label htmlFor="search-id">Search Products</label>
      <input
        onChange={searchQueryChange}
        value={query}
        placeholder={"Search products, categories..."}
        type="text"
        id="search-id"
        name="search"
        ref={inputRef}
      />
      <button className={classes.submitBtn}>
        <SearchIcon />
      </button>
      <ul className={promptClasses.join(" ")} ref={ulRef}>
        <li>{query}</li>
        <li>{query}</li>
        <li>{query}</li>
        <li>{query}</li>
        <li>{query}</li>
        <li>{query}</li>
      </ul>
    </form>
  );
};

export default SearchForm;
