import React, { useState } from "react";
import classes from "./categoriesMenu.module.scss";
import AngleDown from "../../trifles/icons/AngleDown";
import DropDownMenu from "./DropDownMenu";

const CategoriesMenu = ({ data }) => {
  const [dropDown, setDropDown] = useState(false);
  const [pointActive, setPointActive] = useState(false);
  const [activePointValue, setActivePointValue] = useState("");

  const categories = data.map((item) => item.title);

  const red = [classes.point];

  const pointOff = () => {
    setActivePointValue("");
    setDropDown(false);
    setPointActive(false);
  };

  return (
    <div className={classes.wrapper}>
      <div className={classes.menu}>
        {categories.map((point) => (
          <div
            className={red.join(" ")}
            key={`menus${point}`}
            onMouseEnter={() => {
              setActivePointValue(point);
              setDropDown(true);
              setPointActive(true);
              console.log(point);
            }}
            onMouseLeave={pointOff}
          >
            {point}
            <AngleDown />
          </div>
        ))}
      </div>
      <DropDownMenu dropDown={dropDown} data={data[activePointValue]} />
    </div>
  );
};

export default CategoriesMenu;
