import React, { useState, useRef } from "react";
import classes from "./menus.module.scss";
import AngleDown from "../../trifles/icons/AngleDown";

const Menus = (props) => {
  const [listActive, setListActive] = useState(false);

  const menuRef = useRef(null);

  const mouseUp = () => {
    setListActive(true);
  };
  menuRef.addEventListener('mouseup', mouseUp);

  return (
    <div className={classes.wrapper}>
      <div className={classes.menus}>
        {props.points.map((point) => (
          <div className={classes.point} key={`menus${point}`}>
            {point}
            <AngleDown />
          </div>
        ))}
      </div>
      <div className={classes.detailedMenus}>
        <ul>
          {props.points.map((point) => (
            <li ref={menuRef} key={`points${point}`}>{point}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Menus;
