import React, { useState } from "react";
import classes from "./categoriesMenu.module.scss";
import AngleDown from "../../trifles/icons/AngleDown";
import DropDownMenu from "./DropDownMenu";

const CategoriesMenu = ({ data }) => {
  const [dropDown, setDropDown] = useState(false);
  const [pointActive, setPointActive] = useState(false);

  const categories = data.map((item) => item.title);

  const red = [classes.point];
    if (pointActive) {
      red.push(classes.red);
    }

  return (
    <div className={classes.wrapper}>
      <div
        className={classes.menu}
      >
        {categories.map((point) => (
          <div
            className={red.join(" ")}
            key={`menus${point}`}
            onMouseEnter={() => setPointActive(true)}
          >
            {point}
            <AngleDown />
          </div>
        ))}
        <div className={classes.point}></div>
      </div>
      <DropDownMenu
        dropDown={dropDown} data={data}
      />
    </div>
  );
};

export default CategoriesMenu;
