import React, { useState } from "react";
import CarouselItem from "./CarouselItem";
import classes from "./carousel.module.scss";
import { slides } from "./CarouselContent";
import ArrowRight from "./ArrowRight";
import ArrowLeft from "./ArrowLeft";

const Carousel = () => {
  const [clicks, setClicks] = useState(0);
  const [activeSlides, setActiveSlides] = useState(slides.slice(0, 4));
  const [slideClasses, setSlideClasses] = useState([classes.slide]);

  const nextSlides = () => {
  setActiveSlides([...activeSlides, slides.slice(4, 7)]);
  }

  const arrNext = (clicks) => {
    setSlideClasses([...slideClasses, classes.active].join(" "));
  };

  console.log(activeSlides);

  const prevSlides = (clicks) => {
    setActiveSlides(slides.slice(1, 8).concat(activeSlides[0]));
  };

  return (
    <div className={classes.carousel}>
      <div className={classes.wrapper}>
        <ArrowLeft
          className={classes.arrowLeft}
          onClick={() => {
            setClicks(clicks - 1);
            setActiveSlides(nextSlides);
            arrNext(clicks, activeSlides);
          }}
        />
        {activeSlides.map((item) => (
          <CarouselItem
            item={item}
            className={slideClasses}
            key={`carouselItem${slides.indexOf(item)}`}
          />
        ))}
        <ArrowRight
          className={classes.arrowRight}
          onClick={() => {
            setClicks(clicks + 1);
            setActiveSlides(nextSlides);
            arrNext(clicks, activeSlides);
          }}
        />
      </div>
    </div>
  );
};

export default Carousel;
