import classes from "./headerSort.module.scss";
import Search from '../../trifles/icons/Search';

const HeaderSort = () => {
  const categories = [
    "Bakery",
    "Fruits",
    "Meat and fish",
    "Drinks",
    "Kitchen",
    "Special nutrition",
    "Baby",
    "Pharmacy",
  ];

  return (
    <div className={classes.headerSort}>
      <select>
        <option>All categories</option>
        {categories.map((item) => (
          <option key={Date.now() + Math.random()}>{item}</option>
        ))}
      </select>
      <form>
        <label>
          <input
            className={classes.searchInput}
            type="text"
            placeholder="Search Products, categories..."
          />
          <button>
            <Search />
          </button>
        </label>
      </form>
    </div>
  );
};

export default HeaderSort;
