import React from "react";
import MyInput from "../components/MyInput";
import classes from "./checkoutBody.module.scss";
import Sub from "../trifles/Sub";
import MyCheckbox from "../components/MyCheckbox";

const CheckoutBody = () => {
  const formdata1 = [
    { name: "First name", placeholder: "First name" },
    { name: "First name", placeholder: "First name" },
    { name: "First name", placeholder: "First name" },
    { name: "First name", placeholder: "First name" },
  ];

  const formdata2 = [
    { name: "First name", placeholder: "First name" },
    { name: "First name", placeholder: "First name" },
    { name: "First name", placeholder: "First name" },
    { name: "First name", placeholder: "First name" },
  ];

  return (
    <div className={classes.checkoutBody}>
      <form className={classes.col1}>
        <h2>Billing info</h2>
        <div className={classes.sub}>
          <Sub>Please enter your billing info</Sub>
          <Sub>Step 1 of 5</Sub>
        </div>
        <div style={{display: 'flex'}}>
          <div>
          {formdata1.map((item) => (
            <label>
              {item.name}
              <MyInput placeholder={item.placeholder} />
            </label>
          ))}
          </div>
          <div>
          {formdata2.map((item) => (
            <label>
              {item.name}
              <MyInput placeholder={item.placeholder} />
            </label>
          ))}
          </div>
        </div>
        <div className={classes.checkbox}>
          <MyCheckbox label={"Ship to a different address?"} />
        </div>
        <h2>Billing method</h2>
        <div className={classes.sub}>
          <Sub>Please enter your payment method</Sub>
          <Sub>Step 2 of 5</Sub>
        </div>
        <label>
          State / Country
          <MyInput placeholder="State or country" />
        </label>
        <label>
          State / Country
          <MyInput placeholder="State or country" />
        </label>
        <h2>Payment method</h2>
        <div className={classes.sub}>
          <Sub>Please enter your payment method</Sub>
          <Sub>Step 3 of 5</Sub>
        </div>
        <MyInput />
        <MyInput />
      </form>
      <div></div>
    </div>
  );
};

export default CheckoutBody;
