import React from "react";
import classes from "./header.module.scss";
import User from "../../trifles/icons/User";
import Cart from "../../trifles/icons/Cart";
import CartIndicator from "../../trifles/CartIndicator";
import MyInput from "../../components/MyInput";
import Search from "../../trifles/icons/Search";

const Header = () => {

  const [inpStyle, setInpStyle] = useState({color: green});

  return (
    <div className={classes.header}>
      <h1>Freshenescom</h1>
      <form>
        <label>
          <input
            type="text"
            placeholder="Search Products, categories..."
            onChange={() => {
              style = {color: 'red'};
            }}
          />
        </label>
        <button className={classes.searchBtn}>
          <Search />
        </button>
      </form>
      <div className={classes.btns}>
        <button>
          <User style={{ fontSize: 30 }} />
        </button>
        <button>
          <CartIndicator />
          <Cart style={{ fontSize: 30 }} />
        </button>
      </div>
    </div>
  );
};

export default Header;
