import React, {useRef} from "react";
import classes from "./chooseBtn.module.scss";
import Modal from "../modals/Modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown } from "@fortawesome/free-solid-svg-icons";
import ChooseModal from "./ChooseModal";

const ChooseBtn = ({ modal, setModal, data }) => {
  const categories = data.map((obj) => Object.keys(obj)[0]);

  const radioRef = useRef(null);
  const onChange = (e) => {
    e.target.value ? setModal(false) : setModal(true)
  }

  return (
    <>
      <button
        className={classes.chooseBtn}
        onClick={(e) => {
          e.preventDefault();
          setModal(true);
        }}
      >
        All categories
        <FontAwesomeIcon icon={faAngleDown} />
      </button>
      <Modal setVisible={setModal} visible={modal}>
        <ChooseModal categories={categories} ref={radioRef} onChange={onChange}/>
      </Modal>
    </>
  );
};

export default ChooseBtn;
