import React, { useState, useRef } from "react";
import classes from "./header.module.scss";
import User from "../../trifles/icons/User";
import Cart from "../../trifles/icons/Cart";
import CartIndicator from "../../trifles/CartIndicator";
import Search from "../../trifles/icons/Search";
import { Link } from "react-router-dom";
import Modal from "../../components/Modal";
import SearchInput from "../../components/SearchInput";

const Header = () => {
  const [query, setQuery] = useState("");
  const [isShown, setIsShown] = useState(false);
  const [isActive, setIsActive] = useState(false);
  const [modal, setModal] = useState(false);

  const searchQueryChange = (event) => {
    setQuery(event.target.value);

    if (event.target.value != 0) {
      setIsShown(true);
      setIsActive(true);
    } else {
      setIsShown(false);
      setIsActive(false);
    }
  };

  const ulClasses = [classes.list];

  if (isShown === false) {
    ulClasses.push(classes.invisible);
  }

  const inputClasses = [classes.search];

  if (isActive) {
    inputClasses.push(classes.active);
  }

  return (
    <div className={classes.header}>
      <Link className={classes.title} to="/">
        Freshnesecom
      </Link>
      <form>
        <label>
          <SearchInput
            className={inputClasses.join(" ")}
            onChange={searchQueryChange}
            value={query}
            placeholder={"Search products, categories..."}
          />
        </label>
        <button className={classes.searchBtn}>
          <Search />
        </button>
        <ul className={ulClasses.join(" ")}>
          <li>{query}</li>
          <li>{query}</li>
        </ul>
      </form>
      <div className={classes.btns}>
        <button>
          <User style={{ fontSize: 30 }} />
        </button>
        <button>
          <CartIndicator />
          <Cart style={{ fontSize: 30 }} onClick={() => setModal(true)}/>
          <Modal visible={modal} setVisible={setModal}></Modal>
        </button>
      </div>
    </div>
  );
};

export default Header;
