import React, { useState, useRef } from "react";
import classes from "./header.module.scss";
import User from "../../trifles/icons/User";
import Cart from "../../trifles/icons/Cart";
import CartIndicator from "../../trifles/CartIndicator";
import { Link } from "react-router-dom";
import Modal from "../../components/Modal";
import SearchForm from "../../components/SearchForm";

const Header = () => {
  const [modal, setModal] = useState(false);

  return (
    <div className={classes.header}>
      <Link className={classes.title} to="/">
        Freshnesecom
      </Link>
      <SearchForm />
      <div className={classes.btns}>
        <button>
          <User style={{ fontSize: 30 }} onClick={() => setModal(true)} />
        </button>
        <button>
          <CartIndicator />
          <Cart style={{ fontSize: 30 }} onClick={() => setModal(true)} />
          <Modal visible={modal} setVisible={setModal}></Modal>
        </button>
      </div>
    </div>
  );
};

export default Header;
