import React, { useState } from "react";
import classes from "./header.module.scss";
import User from "../../trifles/icons/User";
import Cart from "../../trifles/icons/Cart";
import CartIndicator from "../../trifles/CartIndicator";
import Search from "../../trifles/icons/Search";

const Header = () => {
  const [listStyle, setListStyle] = useState({ display: "block" });
  const [value, setValue] = useState("");

  const inputChange = (e) => {
    setValue(e.target.value);

    if (value !== null) {
      setListStyle({backgroundColor: 'red'})
    } else {
      setListStyle({backgroundColor: 'black'})
    }
  }

  return (
    <div className={classes.header}>
      <h1>Freshenescom</h1>
      <form>
        <label>
          <input
            value={value}
            type="text"
            placeholder="Search Products, categories..."
            onChange={inputChange
            }
          />
          <ul style={listStyle}>
            <li>{value}</li>
            <li>{value}</li>
          </ul>
        </label>
        <button className={classes.searchBtn}>
          <Search />
        </button>
      </form>
      <div className={classes.btns}>
        <button>
          <User style={{ fontSize: 30 }} />
        </button>
        <button>
          <CartIndicator />
          <Cart style={{ fontSize: 30 }} />
        </button>
      </div>
    </div>
  );
};

export default Header;
