import React, { useState } from "react";
import classes from "./categoriesMenu.module.scss";
import AngleDown from "../../trifles/icons/AngleDown";
import DropDownMenu from "./DropDownMenu";

const CategoriesMenu = ({ data }) => {
  const [listActive, setListActive] = useState(false);

  const rootClasses = [classes.detailedMenus];
  if (listActive) {
    rootClasses.push(classes.active);
  }

  const categories = data.map((item) => Object.keys(item)[0]);
  const groups = data.map((group) => {
    for (let name in group) {
      return group[name];
    }
  });

  return (
    <div className={classes.wrapper}>
      <div
        className={classes.menu}
        onMouseEnter={() => setListActive(true)}
        onMouseLeave={() => setListActive(false)}
      >
        {categories.map((point) => (
          <div className={classes.point} key={`menus${point}`}>
            {point}
            <AngleDown />
          </div>
        ))}
      </div>
      <DropDownMenu />
    </div>
  );
};

export default CategoriesMenu;
