import React, { useState, useRef, useEffect } from "react";
import classes from "./searchForm.module.scss";
import SearchIcon from "../../trifles/icons/SearchIcon";
import ChooseBtn from "./ChooseBtn";

const SearchForm = ({ modal, setModal }) => {
  const [query, setQuery] = useState("");
  const [formActive, setFormActive] = useState(false);
  const dropDownClasses = [classes.dropDown];
  const formClasses = [classes.searchForm];
  const ulRef = useRef(null);

  const queryChange = (event) => {
    setQuery(event.target.value);
    event.target.value !== "" ? setFormActive(true) : setFormActive(false);
  };

  useEffect(() => {
    const onClick = (e) => {
      if (!ulRef.current.contains(e.target)) {
        setFormActive(false);
      }
    };
    document.addEventListener("click", onClick);
    return () => document.removeEventListener("click", onClick);
  }, []);

  formActive
    ? formClasses.push(classes.active)
    : dropDownClasses.push(classes.hidden);

  return (
    <form>
      <div className={formClasses.join(" ")} ref={ulRef}>
        <ChooseBtn modal={modal} setModal={setModal} />
        <input
          placeholder={"Search products, categories..."}
          type="text"
          id="search-id"
          name="search"
          value={query}
          onChange={queryChange}
        />
        <button className={classes.submitBtn}>
          <SearchIcon />
        </button>
        <ul className={dropDownClasses.join(" ")}>
          <li>{query}</li>
          <li>{query}</li>
          <li>{query}</li>
          <li>{query}</li>
          <li>{query}</li>
          <li>{query}</li>
        </ul>
      </div>
    </form>
  );
};

export default SearchForm;
