import React, { useState } from "react";
import CarouselItem from "./CarouselItem";
import classes from "./carousel.module.scss";
import { carousel } from "./CarouselContent";
import ArrowRight from "./ArrowRight";
import ArrowLeft from "./ArrowLeft";
import { TransitionGroup } from "react-transition-group";
import { CSSTransition } from "react-transition-group";

const Carousel = () => {

  const [visibleItems, setVisibleItems] = useState(carousel);

  visibleItems = items.slice(0, 4);
  console.log(visibleItems);

  const arrowRight = (e) => {
    e.preventDefault()
    setVisibleItems([...visibleItems, items.slice(0, 4)])
  }

  return (
    <div className={classes.carousel}>
      <ArrowLeft className={classes.arrowLeft} />
        <TransitionGroup className={classes.wrapper}>
          {items.map((item) => (
            <CSSTransition
              key={Math.random() + Date.now()}
              timeout={500}
              classNames="item"
            >
              <CarouselItem item={item} />
            </CSSTransition>
          ))}
        </TransitionGroup>
      <ArrowRight className={classes.arrowRight} onClick={arrowRight}/>
    </div>
  );
};

export default Carousel;
