import React, { useState, useRef } from "react";
import classes from "./header.module.scss";
import User from "../../trifles/icons/User";
import Cart from "../../trifles/icons/Cart";
import CartIndicator from "../../trifles/CartIndicator";
import Search from "../../trifles/icons/Search";
import { Link } from "react-router-dom";

const Header = () => {
  const [query, setQuery] = useState("");
  const [isShown, setIsShown] = useState({ display: "none" });
  const [isRounded, setIsRounded] = useState({borderRadius: '0 0 12px 12px'})

  const searchQueryChange = (event) => {
    setQuery(event.target.value);

    if (event.target.value != 0) {
      setIsShown({ display: "block" });
      setIsRounded({borderRadius: '12px'})
    } else {
      setIsShown({ display: "none" });
      setIsRounded({borderRadius: '0 0 12px 12px'})
    }
  };

  return (
    <div className={classes.header}>
      <Link className={classes.title} to="/">
        Freshnesecom
      </Link>
      <form>
        <label>
          <input
            style={isRounded}
            onChange={searchQueryChange}
            value={query}
            placeholder={"Search products, categories..."}
          />
        </label>
        <button className={classes.searchBtn}>
          <Search />
        </button>
        <ul style={isShown}>
          <li>{query}</li>
          <li>{query}</li>
        </ul>
      </form>
      <div className={classes.btns}>
        <button>
          <User style={{ fontSize: 30 }} />
        </button>
        <button>
          <CartIndicator />
          <Cart style={{ fontSize: 30 }} />
        </button>
      </div>
    </div>
  );
};

export default Header;
