import React from "react";
import classes from "./chooseBtn.module.scss";
import AngleDown from "../../trifles/icons/AngleDown";
import Modal from "../modals/Modal";

const ChooseBtn = ({ modal, setModal }) => {
  const categories = [
    "Bakery",
    "Fruit and vegetables",
    "Meat and fish",
    "Drinks",
    "Kitchen",
    "Special nutrition",
    "Baby",
    "Pharmacy",
  ];

  return (
    <>
      <button
        className={classes.chooseBtn}
        onClick={(e) => {
          e.preventDefault();
          setModal(true);
        }}
      >
        All categories
        <AngleDown className={classes.angle} />
      </button>
      <Modal visible={modal} setVisible={setModal} style={"centered"}>
        <div className={classes.modal}>
            {categories.map((item) => (
              <div key={item}>
                <input name="category" type="radio" onClick={() => {setModal(false)}}/>
                <label htmlFor={`${item}-id}`}>{item}</label>
              </div>
            ))}
        </div>
      </Modal>
    </>
  );
};

export default ChooseBtn;
