import React, { useState, useRef } from "react";
import classes from "./header.module.scss";
import User from "../../trifles/icons/User";
import Cart from "../../trifles/icons/Cart";
import CartIndicator from "../../trifles/CartIndicator";
import Search from "../../trifles/icons/Search";
import SearchInput from "../../components/SearchInput";

const Header = () => {
  const [value, setValue] = useState("");
  const [isActive, setIsActive] = useState(false);
  const [isShown, setIsShown] = useState({ display: "none" });

  const inputChange = (event) => {
    setValue(event.target.value);
    setIsShown({display: 'block'})
  };

  console.log(Object.prototype.input)

  return (
    <div className={classes.header}>
      <h1>{value}</h1>
      <form>
        <label>
          <input onChange={inputChange} value={value} />
        </label>
        <button className={classes.searchBtn}>
          <Search />
        </button>
        <ul style={isShown}>
          <li>{value}</li>
          <li>{value}</li>
        </ul>
      </form>
      <div className={classes.btns}>
        <button>
          <User style={{ fontSize: 30 }} />
        </button>
        <button>
          <CartIndicator />
          <Cart style={{ fontSize: 30 }} />
        </button>
      </div>
    </div>
  );
};

export default Header;
