import React, { useState, useRef, useEffect } from "react";
import classes from "./searchForm.module.scss";
import SearchIcon from "../trifles/icons/SearchIcon";

const SearchForm = () => {
  const [query, setQuery] = useState("");
  const [promptShown, setPromptShown] = useState(false);
  const [inputActive, setInputActive] = useState(false);

  const searchQueryChange = (event) => {
    setQuery(event.target.value);

    if (event.target.value != 0) {
      setPromptShown(true);
      setInputActive(true);
    } else {
      setPromptShown(false);
      setInputActive(false);
    }
  };

  const rootEl = useRef(null);

  useEffect(() => {
    const onClick = (e) =>
      rootEl.current.contains(e.target) || console.log("клик вне компонента");
    document.addEventListener("click", onClick);
    return () => document.removeEventListener("click", onClick);
  }, []);

  const promptClasses = [classes.prompt];

  if (promptShown === false) {
    promptClasses.push(classes.hidden);
  }

  const inputClasses = [classes.search];

  if (inputActive) {
    inputClasses.push(classes.active);
  }

  return (
    <form ref={rootEl} className={classes.searchForm}>
      <label htmlFor="search-id">Search products, categories</label>
      <input
        className={inputClasses.join(" ")}
        onChange={searchQueryChange}
        value={query}
        placeholder={"Search products, categories..."}
        type="text"
        id="search-id"
        name="search"
      />
      <button className={classes.btn}>
        <SearchIcon />
      </button>
      <ul className={promptClasses.join(" ")}>
        <li>{query}</li>
        <li>{query}</li>
      </ul>
    </form>
  );
};

export default SearchForm;
